const copiedText=document.getElementById("copy-success").textContent;const initCopyText=document.getElementById("copy-init").textContent;const changeIcon=(e,t)=>{e.classList.add(t);e.setAttribute("aria-label",copiedText);setTimeout(()=>{e.classList.remove(t);e.setAttribute("aria-label",initCopyText)},2500)};const addCopyEventListenerToDiv=(e,t)=>{e.addEventListener("click",()=>copyCodeAndChangeIcon(e,t))};const copyCodeAndChangeIcon=async(t,e)=>{const o=e.querySelector("table")?getTableCode(e):getNonTableCode(e);try{await navigator.clipboard.writeText(o);changeIcon(t,"checked")}catch(e){changeIcon(t,"error")}};const getNonTableCode=e=>{return[...e.querySelectorAll("code")].map(e=>e.textContent).join("")};const getTableCode=e=>{return[...e.querySelectorAll("tr")].map(e=>e.querySelector("td:last-child")?.innerText??"").join("")};document.querySelectorAll("pre").forEach(e=>{const t=document.createElement("div");t.setAttribute("role","button");t.setAttribute("aria-label",initCopyText);t.setAttribute("title",initCopyText);t.className="copy-code";e.prepend(t);addCopyEventListenerToDiv(t,e)});